(function () {
	"use strict";
	module.exports = function calculation (values, func, converter) {
		if (converter)
			values = converter(values);
		if (values instanceof Array) {
			var result = [];
			for (var i = 0, l = values.length; i < l; ++i)
				result.push(func(values[i]));
			return result;
		}
		return func(values);
	}
})()