(function () {
	"use strict";
	exports.lb = function lb (v) {
		return Math.log(v) / Math.log(2);
	}

	exports.lg = function lg (v) {
		return Math.log(v) / Math.log(10);
	}

	exports.ln = function ln (v) {
		return Math.log(v);
	}

	exports.log = function log (v, p) {
		return Math.log(v) / Math.log(p);
	}
})()