(function () {
	"use strict";
	exports.mean = function mean () {
		var sum = 0,
		    l = arguments.length;
		for (var i = 0; i < l; ++i)
			sum += arguments[i];
		return sum / l;
	};

	exports.product = function product (x, y) {
		var l = Math.max(x.length, y.length),
		    result = [];
		for (var i = 0; i < l; ++i)
			result.push(x[i] * y[i]);
		return result;
	};

	exports.sigma = function (x, y) {
		if (typeof x === 'number')
			x = [x];
		if (typeof y === 'number')
			y = [y];

		var l = Math.min(x.length, y.length),
		    res = 0;
		for (var i = 0; i < l; ++i)
			res += Math.pow(x[i] - y[i], 2);
		return Math.sqrt(res / l);
	}
})()